import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { Link, NavLink } from "react-router-dom";
import { useContext } from "react";
import UserContext from "../context/UserContext";

export default function AppNavbar() {
  const { user } = useContext(UserContext);

  return (
    <Navbar expand="lg" className="bg-light">
      <Container className="ms-0">
        <Navbar.Brand as={NavLink} to="/">The Movie App</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={NavLink} to="/" exact="true">Home</Nav.Link>
            <Nav.Link as={NavLink} to="/movies" exact="true">Movies</Nav.Link>
            <Nav.Link as={NavLink} to="/genres" exact="true">Genres</Nav.Link>

            {user.id !== null ? 
              user.isAdmin ? (
                <>
                  <Nav.Link as={Link} to="/admin">Admin Panel</Nav.Link>
                  <Nav.Link as={Link} to="/logout">Logout</Nav.Link>
                </>
              ) : (
                <>
                  <Nav.Link as={NavLink} to="/profile" exact="true">Profile</Nav.Link>
                  <Nav.Link as={NavLink} to="/watchlist" exact="true">Watchlist</Nav.Link>
                  <Nav.Link as={NavLink} to="/logout">Logout</Nav.Link>
                </>
              ) 
              : (
                <>
                  <Nav.Link as={NavLink} to="/login" exact="true">Login</Nav.Link>
                  <Nav.Link as={NavLink} to="/register" exact="true">Register</Nav.Link>
                </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
