import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { Link, NavLink } from "react-router-dom";
import { useContext } from "react";
import UserContext from "../context/UserContext";

export default function AppNavbar() {
  const { user } = useContext(UserContext);

  return (
    <Navbar expand="lg" className="bg-light">
      <Container className="ms-0">
        <Navbar.Brand as={NavLink} to="/">The Movie App</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={NavLink} to="/" exact>Home</Nav.Link>
            <Nav.Link as={NavLink} to="/movies" exact>Movies</Nav.Link>
            <Nav.Link as={NavLink} to="/genres" exact>Genres</Nav.Link>

            {user.id ? ( // Check if the user is logged in
              user.isAdmin ? ( // Check if the user is an admin
                <>
                  <Nav.Link as={NavLink} to="/admin" exact>Admin Panel</Nav.Link>
                  <Nav.Link as={NavLink} to="/logout" exact>Logout</Nav.Link>
                </>
              ) : ( // Regular user links
                <>
                  <Nav.Link as={NavLink} to="/profile" exact>Profile</Nav.Link>
                  <Nav.Link as={NavLink} to="/watchlist" exact>Watchlist</Nav.Link>
                  <Nav.Link as={NavLink} to="/logout" exact>Logout</Nav.Link>
                </>
              )
            ) : ( // If not logged in
              <>
                <Nav.Link as={NavLink} to="/login" exact>Login</Nav.Link>
                <Nav.Link as={NavLink} to="/register" exact>Register</Nav.Link>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
