import React, { useContext } from "react";
import { Link, NavLink } from "react-router-dom";
import { Container, Navbar, Nav } from "react-bootstrap";
import UserContext from "../context/UserContext";

export default function AppNavbar() {
  const { user } = useContext(UserContext);

  // Inline styles
  const navbarStyle = {
    backgroundColor: '#282c34', // Dark background color
  };

  const linkStyle = {
    color: '#ffffff', // White text color
    transition: 'color 0.3s', // Smooth color transition
  };

  const linkHoverStyle = {
    color: '#61dafb', // Light blue on hover
  };

  const brandStyle = {
    fontSize: '1.5rem', // Larger brand font size
    fontWeight: 'bold', // Bold brand text
    color: '#61dafb', // Brand color
  };

  return (
    <Navbar expand="lg" style={navbarStyle}>
      <Container className="ms-0">
        <Navbar.Brand as={NavLink} to="/" style={brandStyle}>The Movie App</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={NavLink} to="/" exact="true" style={linkStyle}>Home</Nav.Link>
            <Nav.Link as={NavLink} to="/movies" exact="true" style={linkStyle}>Movies</Nav.Link>
            <Nav.Link as={NavLink} to="/genres" exact="true" style={linkStyle}>Genres</Nav.Link>

            {user.id !== null ? 
              user.isAdmin ? (
                <>
                  <Nav.Link as={Link} to="/admin" style={linkStyle}>Admin Panel</Nav.Link>
                  <Nav.Link as={Link} to="/logout" style={linkStyle}>Logout</Nav.Link>
                </>
              ) : (
                <>
                  <Nav.Link as={NavLink} to="/profile" exact="true" style={linkStyle}>Profile</Nav.Link>
                  <Nav.Link as={NavLink} to="/watchlist" exact="true" style={linkStyle}>Watchlist</Nav.Link>
                  <Nav.Link as={NavLink} to="/logout" style={linkStyle}>Logout</Nav.Link>
                </>
              ) 
              : (
                <>
                  <Nav.Link as={NavLink} to="/login" exact="true" style={linkStyle}>Login</Nav.Link>
                  <Nav.Link as={NavLink} to="/register" exact="true" style={linkStyle}>Register</Nav.Link>
                </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
