import React, { useContext } from "react";
import { Link, NavLink } from "react-router-dom";
import { Container, Navbar, Nav } from "react-bootstrap";
import UserContext from "../context/UserContext";

export default function AppNavbar() {
  const { user } = useContext(UserContext);

  return (
    <Navbar expand="lg" style={{ backgroundColor: '#282c34' }}>
      <Container className="ms-0">
        <Navbar.Brand as={NavLink} to="/" style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#61dafb' }}>The Movie App</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={NavLink} to="/" exact style={{ color: '#ffffff', transition: 'color 0.3s' }}>Home</Nav.Link>
            <Nav.Link as={NavLink} to="/movies" exact style={{ color: '#ffffff', transition: 'color 0.3s' }}>Movies</Nav.Link>
            <Nav.Link as={NavLink} to="/genres" exact style={{ color: '#ffffff', transition: 'color 0.3s' }}>Genres</Nav.Link>

            {user.id !== null ? 
              user.isAdmin ? (
                <>
                  <Nav.Link as={Link} to="/admin" style={{ color: '#ffffff', transition: 'color 0.3s' }}>Admin Panel</Nav.Link>
                  <Nav.Link as={Link} to="/logout" style={{ color: '#ffffff', transition: 'color 0.3s' }}>Logout</Nav.Link>
                </>
              ) : (
                <>
                  <Nav.Link as={NavLink} to="/profile" exact style={{ color: '#ffffff', transition: 'color 0.3s' }}>Profile</Nav.Link>
                  <Nav.Link as={NavLink} to="/watchlist" exact style={{ color: '#ffffff', transition: 'color 0.3s' }}>Watchlist</Nav.Link>
                  <Nav.Link as={NavLink} to="/logout" style={{ color: '#ffffff', transition: 'color 0.3s' }}>Logout</Nav.Link>
                </>
              ) 
              : (
                <>
                  <Nav.Link as={NavLink} to="/login" exact style={{ color: '#ffffff', transition: 'color 0.3s' }}>Login</Nav.Link>
                  <Nav.Link as={NavLink} to="/register" exact style={{ color: '#ffffff', transition: 'color 0.3s' }}>Register</Nav.Link>
                </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
